package com.sphereon.oid.fed.common.op

import com.sphereon.oid.fed.common.mapper.JsonMapper
import io.ktor.client.engine.mock.*
import io.ktor.http.*
import kotlin.test.Test
import kotlin.test.assertEquals

class TrustChainValidationTest {

    private val intermediateEntityStatement = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..dRJSJIhDkB6LmqJ7iKsacmWTKYnSJDy9X8KRpz3XZq0"
    private val intermediateEntityStatement1 = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..HUuT8CtSWpax6Dbu8THPei16UDVEylUyESuXE3-PdPo"
    private val trustAnchor = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..CxbYkZxQ8Y8U0uLBn5uAl1xJh96C8qv_4RB6cOIDvss"

    private val mockEngine = MockEngine { request ->
        when(request.url) {
            Url("https://edugain.org/federation") -> respond(
                content = intermediateEntityStatement,
                status = HttpStatusCode.OK,
                headers = headersOf(HttpHeaders.ContentType, "application/entity-statement+jwt")
            )
            Url("https://edugain.org/federation_one") -> respond(
                content = intermediateEntityStatement1,
                status = HttpStatusCode.OK,
                headers = headersOf(HttpHeaders.ContentType, "application/entity-statement+jwt")
            )
            Url("https://edugain.org/federation_two") -> respond(
                content = trustAnchor,
                status = HttpStatusCode.OK,
                headers = headersOf(HttpHeaders.ContentType, "application/entity-statement+jwt")
            )
            Url("https://edugain.org/federation_three") -> respond(
                content = trustAnchor,
                status = HttpStatusCode.OK,
                headers = headersOf(HttpHeaders.ContentType, "application/entity-statement+jwt")
            )
            else -> error("Unhandled ${request.url}")
        }
    }

    @Test
    fun readAuthorityHintsTest() {
        val entityConfigurationStatement = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..8O9EVsFWRo65ITGDp2KS5sVs7PNOBEWPm60mcOyC29A"
        val listOfEntityConfigurationStatementList = listOf(
            listOf(JsonMapper().mapEntityStatement(intermediateEntityStatement), JsonMapper().mapEntityStatement(intermediateEntityStatement1), JsonMapper().mapEntityStatement(trustAnchor)),
            listOf(JsonMapper().mapEntityStatement(intermediateEntityStatement), JsonMapper().mapEntityStatement(trustAnchor))
        )
        assertEquals(listOfEntityConfigurationStatementList, readAuthorityHints(entityConfigurationStatement, mockEngine))
    }
}
